import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./pages/LoginPage";
import UserInfoPage from "./pages/UserInfoPage";
import NutritionPage from "./pages/NutritionPage";
import NavigationBar from "./components/NavigationBar";

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false); // 로그인 여부 상태
  const [isInfoComplete, setIsInfoComplete] = useState(false); // 유저 정보 입력 여부 상태

  const handleLoginSuccess = () => {
    setIsAuthenticated(true); // 로그인 성공 시 상태 변경
    console.log("로그인 성공: isAuthenticated 상태 업데이트");
  };

  const handleInfoComplete = () => {
    setIsInfoComplete(true); // 유저 정보 입력 완료 시 상태 변경
    console.log("유저 정보 입력 완료: isInfoComplete 상태 업데이트");
  };

  return (
    <Router>
      <div style={{ paddingBottom: "80px" }}>
        <Routes>
          {/* 초기 화면: LoginPage */}
          <Route
            path="/"
            element={
              isAuthenticated ? (
                isInfoComplete ? (
                  <Navigate to="/nutrition" replace />
                ) : (
                  <Navigate to="/userinfo" replace />
                )
              ) : (
                <LoginPage onLoginSuccess={handleLoginSuccess} />
              )
            }
          />
          <Route
            path="/userinfo"
            element={
              isAuthenticated ? (
                <UserInfoPage onComplete={handleInfoComplete} />
              ) : (
                <Navigate to="/" replace />
              )
            }
          />
          <Route
            path="/nutrition"
            element={
              isInfoComplete ? (
                <NutritionPage />
              ) : (
                <Navigate to="/userinfo" replace />
              )
            }
          />
        </Routes>
        {isAuthenticated && <NavigationBar />} {/* 로그인 후에만 네비게이션 바 표시 */}
      </div>
    </Router>
  );
};

export default App;
