import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';  // 기본 스타일 import

const ChallengePage = () => {
  const [date, setDate] = useState(new Date()); // 기본 날짜를 오늘로 설정

  const handleDateChange = (newDate) => {
    setDate(newDate);  // 사용자가 선택한 날짜로 상태 업데이트
  };

   // 날짜 셀에 커스텀 클래스 이름을 적용하는 함수
  const getTileClassName = ({ date, view }) => {
    // 요일을 구합니다. (0: 일요일, 1: 월요일, ..., 6: 토요일)
    const dayOfWeek = date.getDay();

    // 평일(월요일~금요일)에 대해 'weekday' 클래스를 추가
    if (dayOfWeek >= 1 && dayOfWeek <= 5) {
      return 'weekday';  // 평일에 대해 weekday 클래스를 추가
    }
    return '';  // 주말은 기본 스타일 유지
  };

  return (
    <div className="App">
      <h1>챌린지 관리 페이지</h1>

      {/* 기본 달력 컴포넌트 */}
      <Calendar
        onChange={handleDateChange}  // 날짜 변경 시 처리할 함수
        value={date}  // 현재 선택된 날짜
        tileClassName={getTileClassName}    // 날짜마다 클래스 추가

      />
      <style>
        {`
          .weekday {
            background-color: #fdfdfd;  /* 원하는 색상으로 변경 */
            color: #ff5733;  /* 글씨 색상 변경 */
            font-weight: bold;
          }
        `}
      </style>
    </div>
  );
};

export default ChallengePage;
