import React from "react";
import "./HabitPage.css";

export const HabitPage = () => {
  // 챌린지 데이터 (참여 중 / 참여 가능 상태 포함)
  const [challenges, setChallenges] = useState([
    { id: 1, title: '매일 운동', status: 'available' },
    { id: 2, title: '건강식 챌린지', status: 'available' },
    { id: 3, title: '물 많이 마시기', status: 'participating' },
  ]);

  // 월요일 체크
  const isMonday = new Date().getDay() === 1;

  // 챌린지 참여 함수
  const joinChallenge = (id) => {
    if (!isMonday) {
      alert("월요일에 참여해주세요!");
      // 월요일이 아니면 신청 페이지로 이동 (예: `/join`으로 리디렉션)
      return;
    }

    // 참여 가능 목록에서 선택된 챌린지를 참여 중으로 변경
    setChallenges(prevChallenges =>
      prevChallenges.map(challenge =>
        challenge.id === id && challenge.status === 'available'
          ? { ...challenge, status: 'participating' }
          : challenge
      )
    );
  };

  // 참여 중 챌린지 목록
  const participatingChallenges = challenges.filter(challenge => challenge.status === 'participating');
  
  // 참여 가능 챌린지 목록
  const availableChallenges = challenges.filter(challenge => challenge.status === 'available');

  return (
    <div className="App">
      <h1>습관 챌린지</h1>
      
      <div className="section">
        <h2>참여 중</h2>
        <ul>
          {participatingChallenges.length > 0 ? (
            participatingChallenges.map(challenge => (
              <li key={challenge.id}>
                {challenge.title} (참여 중)
              </li>
            ))
          ) : (
            <li>참여 중인 챌린지가 없습니다.</li>
          )}
        </ul>
      </div>

      <div className="section">
        <h2>참여 가능</h2>
        <ul>
          {availableChallenges.length > 0 ? (
            availableChallenges.map(challenge => (
              <li key={challenge.id}>
                {challenge.title}
                <button onClick={() => joinChallenge(challenge.id)}>참여하기</button>
              </li>
            ))
          ) : (
            <li>참여 가능한 챌린지가 없습니다.</li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default HabitPage;
