import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import "./HabitPage.css";

export const HabitPage = () => {
  const [searchParams] = useSearchParams();
  const completedId = searchParams.get('completedId');

  const [challenges, setChallenges] = useState([
    { id: 1, title: '매일 운동', status: 'available' },
    { id: 2, title: '건강식 챌린지', status: 'available' },
    { id: 3, title: '물 많이 마시기', status: 'participating' },
  ]);

  const isMonday = new Date().getDay() === 1;

  const joinChallenge = (id) => {
    if (isMonday) {
      // 월요일이면 신청 페이지로 넘어감
      // completedId로 다시 돌아오면 참여 상태로 바꿔줄 것
      window.location.href = `/join?id=${id}`;
    } else {
      // 월요일이 아니면 경고만 표시
      alert("월요일에 참여해주세요!");
    }
  };

  // 신청 완료 후 참여 상태로 변경하는 함수
  const completeJoinChallenge = (id) => {
    setChallenges(prevChallenges =>
      prevChallenges.map(challenge =>
        challenge.id === Number(id) && challenge.status === 'available'
          ? { ...challenge, status: 'participating' }
          : challenge
      )
    );
  };

  // completedId 파라미터가 있으면 해당 챌린지 참여 상태 변경
  useEffect(() => {
    if (completedId) {
      completeJoinChallenge(completedId);
    }
  }, [completedId]);

  const participatingChallenges = challenges.filter(challenge => challenge.status === 'participating');
  const availableChallenges = challenges.filter(challenge => challenge.status === 'available');

  return (
    <div className="App">
      <h1>습관 챌린지</h1>
      
      <div className="section">
        <h2>참여 중</h2>
        <ul>
          {participatingChallenges.length > 0 ? (
            participatingChallenges.map(challenge => (
              <li key={challenge.id}>
                <button disabled>{challenge.title} (참여 중)</button>
              </li>
            ))
          ) : (
            <li>
              <button disabled>참여 중인 챌린지가 없습니다.</button>
            </li>
          )}
        </ul>
      </div>

      <div className="section">
        <h2>참여 가능</h2>
        <ul>
          {availableChallenges.length > 0 ? (
            availableChallenges.map(challenge => (
              <li key={challenge.id}>
                <button onClick={() => joinChallenge(challenge.id)}>
                  {challenge.title}
                </button>
              </li>
            ))
          ) : (
            <li>
              <button disabled>참여 가능한 챌린지가 없습니다.</button>
            </li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default HabitPage;
