import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UserInfoPage.css";
import { sendUserInfoToBackend } from "../api/backend"; // 백엔드 요청 함수

const UserInfoPage = ({ onComplete }) => {
  const [formData, setFormData] = useState({
    nickname: "",
    height: "",
    weight: "",
    gender: "",
    purpose: "",
    activity: "",
  });

  const navigate = useNavigate(); // 페이지 이동에 사용할 네비게이션 훅

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async () => {
    const { nickname, height, weight, gender, purpose, activity } = formData;

    if (!nickname || !height || !weight || !gender || !purpose || !activity) {
      alert("정보를 더 입력해 주세요.");
      console.log("입력 미완료: 일부 필드가 비어 있습니다.");
    } else {
      alert("확인 완료!");
      console.log("입력 완료: 모든 필드가 입력되었습니다.");

      const snsToken = localStorage.getItem("snsToken"); // 카카오 로그인에서 받은 토큰
      if (!snsToken) {
        alert("로그인이 필요합니다. 다시 로그인하세요.");
        return;
      }

      try {
        // 백엔드에 데이터 전송
        await sendUserInfoToBackend({
          snsToken,
          nickname,
          height,
          weight,
          sex: gender === "남성" ? "male" : "female",
          type: purpose === "다이어트" ? "diet" : purpose === "체중유지" ? "maintain" : "bulk",
          activityLevel:
            activity === "아주 적음"
              ? "sedentary"
              : activity === "조금 적음"
              ? "light"
              : activity === "보통"
              ? "moderate"
              : activity === "조금 많음"
              ? "active"
              : "very_active",
        });

        onComplete(); // App.js의 상태 변경
        navigate("/nutrition"); // NutritionPage로 이동
      } catch (error) {
        console.error("백엔드 요청 실패:", error);
        alert("정보 저장에 실패했습니다. 다시 시도하세요.");
      }
    }
  };

  return (
    <div className="user-info-container">
      <h1>사용자 정보 입력</h1>
      <h2>Hab-eat 사용을 위한 기본 정보를 입력해주세요</h2>

      <label>닉네임</label>
      <input
        type="text"
        name="nickname"
        value={formData.nickname}
        onChange={handleInputChange}
        placeholder="닉네임을 입력하세요"
      />

      <label>키 (cm)</label>
      <input
        type="number"
        name="height"
        value={formData.height}
        onChange={handleInputChange}
        placeholder="키를 입력하세요"
      />

      <label>몸무게 (kg)</label>
      <input
        type="number"
        name="weight"
        value={formData.weight}
        onChange={handleInputChange}
        placeholder="몸무게를 입력하세요"
      />

      <label>성별</label>
      <div className="radio-group">
        <label>
          <input
            type="radio"
            name="gender"
            value="남성"
            checked={formData.gender === "남성"}
            onChange={handleInputChange}
          />
          남성
        </label>
        <label>
          <input
            type="radio"
            name="gender"
            value="여성"
            checked={formData.gender === "여성"}
            onChange={handleInputChange}
          />
          여성
        </label>
      </div>

      <label>사용 목적</label>
      <select name="purpose" value={formData.purpose} onChange={handleInputChange}>
        <option value="">목적을 선택하세요</option>
        <option value="다이어트">다이어트</option>
        <option value="체중유지">체중유지</option>
        <option value="벌크업">벌크업</option>
      </select>

      <label>운동량</label>
      <select name="activity" value={formData.activity} onChange={handleInputChange}>
        <option value="">운동량을 선택하세요</option>
        <option value="아주 적음">아주 적음</option>
        <option value="조금 적음">조금 적음</option>
        <option value="보통">보통</option>
        <option value="조금 많음">조금 많음</option>
        <option value="아주 많음">아주 많음</option>
      </select>

      <button onClick={handleSubmit}>등록 완료</button>
    </div>
  );
};

export default UserInfoPage;
