import { useState } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';

export const JoinPage = () => {
  const [searchParams] = useSearchParams();
  const id = searchParams.get('id');
  const navigate = useNavigate();
  
  // 숫자 입력 상태 관리
  const [goalDays, setGoalDays] = useState('');

  // 숫자 입력을 처리하는 함수
  const handleInputChange = (event) => {
    const value = event.target.value;

    // 1~7 사이의 숫자만 허용
    if (value === '' || (value >= 1 && value <= 7)) {
      setGoalDays(value);
    }
  };

  // 신청 완료 함수
  const handleComplete = () => {
    if (!goalDays) {
      alert("목표 일수를 1에서 7 사이로 입력해주세요.");
      return;
    }

    // 신청 완료 로직 수행 후 HabitPage로 돌아가면서 "신청 완료" 상태 전달
    navigate(`/habit/?completedId=${id}`);
  };

  return (
    <div className='App'>
      <h1>챌린지 신청</h1>
      <div className='goal'>일주일에 며칠을 목표로 하시나요?<br></br> (1~7일 사이)</div>
      <div className='goal'>
        <input
            type="number" 
            min="1" 
            max="7" 
            value={goalDays} 
            onChange={handleInputChange} 
            placeholder="1~7일" 
        />
      </div>
      
      <button onClick={handleComplete}>신청 완료</button>
    </div>
  );
};
