import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './NutritionPage.css';

const NutritionPage = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  return (
    <div className="nutrition-container">
      {/* 달력 버튼 및 선택한 날짜 */}
      <div className="calendar-section">
        <button className="calendar-button" onClick={() => alert('달력 열기')}>
          📅
        </button>
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          inline
          className="hidden-datepicker"
        />
        <span className="selected-date">
          {selectedDate.toLocaleDateString('ko-KR')}
        </span>
      </div>

      {/* 영양 정보 섹터 */}
      <div className="nutrition-info">
        <p>
          섭취 칼로리: <b>1500</b> / 권장 칼로리: <b>2000</b> kcal
        </p>
        <p>
          섭취 단백질: <b>50g</b> / 권장 단백질: <b>75g</b>
        </p>
        <p>탄수화물: 200g | 지방: 50g | 당: 20g</p>
      </div>

      {/* 식사 섹터 */}
      <div className="meal-section">
        <div className="meal-time">
          <h3>아침</h3>
          <div className="meal-items">등록된 음식이 없습니다.</div>
        </div>
        <div className="meal-time">
          <h3>점심</h3>
          <div className="meal-items">등록된 음식이 없습니다.</div>
        </div>
        <div className="meal-time">
          <h3>저녁</h3>
          <div className="meal-items">등록된 음식이 없습니다.</div>
        </div>
      </div>

      {/* 네비게이터 바 */}
      <div className="navigator">
        <Link to="/nutrition" className="navigator-item active">
          식단
        </Link>
        <button className="navigator-item camera">📷</button>
        <Link to="/habit" className="navigator-item">
          습관
        </Link>
        <Link to="/mypage" className="navigator-item mypage">
          마이페이지
        </Link>
      </div>
    </div>
  );
};

export default NutritionPage;
