import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import "./JoinPage.css";
import back from "../img/back.svg";

const JoinPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  
  // HabitPage에서 navigate 시 state로 전달한 id 받아오기
  const { id } = location.state || {};

  const [goalDays, setGoalDays] = useState('');
  const [loading, setLoading] = useState(false); // 로딩 상태 관리

  // 숫자 입력을 처리하는 함수
  const handleInputChange = (event) => {
    const value = event.target.value;

    // 1~7 사이의 숫자만 허용
    if (value === '' || (value >= 1 && value <= 7)) {
      setGoalDays(value);
    }
  };

  // 완료 버튼 클릭 시 호출되는 함수
  const handleComplete = async () => {
    if (!goalDays) {
      alert("목표 일수를 1에서 7 사이로 입력해주세요.");
      return;
    }

    try {
      setLoading(true);

      // POST 요청 보내기
      const response = await fetch(`http://localhost:3001/challenges/${id}/participants`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          goalDays: parseInt(goalDays), // 목표 일수
        }),
      });

      // 응답 처리
      if (!response.ok) {
        throw new Error('챌린지 참여 신청에 실패했습니다.');
      }

      const data = await response.json();
      console.log('챌린지 참여 성공:', data);

      // 참여 완료 시 HabitPage로 돌아가며 API 응답 데이터를 전달
      navigate('/habit', { state: { participatedChallengeId: id } });
    } catch (error) {
      console.error('Error:', error);
      alert('챌린지 참여 신청 중 문제가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className='App'>
      <div className='div'>
        <div className="settings-header">
          <img
            className="settings-back-icon"
            alt="Back"
            src={back}
            onClick={() => navigate(-1)} // 뒤로가기
          />
        </div>
        <h1>챌린지 참여 신청</h1>
      </div>
      <div className='section'>
        <p className='goal'>일주일 동안 며칠을 목표로 하시나요?</p>
        <input 
          type="number" 
          min="1" 
          max="7" 
          value={goalDays} 
          onChange={handleInputChange} 
          placeholder="1~7일" 
          disabled={loading} // 로딩 중일 때 입력 비활성화
        />
      </div>
      <button onClick={handleComplete} disabled={loading}>
        {loading ? '처리 중...' : '완료'}
      </button>
    </div>
  );
};

export default JoinPage;
