import React, { useState } from 'react';
import Calendar from 'react-calendar';  // react-calendar import
import 'react-calendar/dist/Calendar.css';  // Ïä§ÌÉÄÏùº import

const ChallengePage = () => {
  const [date, setDate] = useState(new Date()); // ÏÑ†ÌÉùÎêú ÎÇ†Ïßú
  const [completedChallenges, setCompletedChallenges] = useState([
    { date: '2024-12-01', challengeId: 1 }, // ÏòàÏãú Ï±åÎ¶∞ÏßÄ ÏôÑÎ£å ÎÇ†Ïßú
    { date: '2024-12-05', challengeId: 2 },
  ]);

  const handleDateChange = (newDate) => {
    setDate(newDate);  // ÏÑ†ÌÉùÎêú ÎÇ†Ïßú ÏóÖÎç∞Ïù¥Ìä∏
  };

  // Ï±åÎ¶∞ÏßÄÎ•º Ïã§ÌñâÌïú ÎÇ†ÏßúÏóê Ìï¥Îπó Î°úÍ≥†Î•º ÌëúÏãúÌï† Ïàò ÏûàÎèÑÎ°ù Ìï®Ïàò ÏûëÏÑ±
  const tileContent = ({ date, view }) => {
    const dateStr = date.toISOString().split('T')[0];  // YYYY-MM-DD ÌòïÏãùÏúºÎ°ú ÎÇ†Ïßú Î≥ÄÌôò
    const completedChallenge = completedChallenges.find(challenge => challenge.date === dateStr);
    
    return completedChallenge ? (
      <div className="habit-icon">üèÖ</div>  // Ìï¥Îπó Î°úÍ≥† (ÏòàÏãúÎ°ú üèÖ ÏïÑÏù¥ÏΩò ÏÇ¨Ïö©)
    ) : null;
  };

  // Ï£ºÏ∞®Î≥Ñ ÌïòÏù¥ÎùºÏù¥Ìä∏ (Îß§Ï£º ÏùºÏöîÏùºÎßàÎã§ Í≤∞ÏÇ∞)
  const getWeekNumber = (date) => {
    const currentDate = new Date(date);
    const day = currentDate.getDay();  // ÌòÑÏû¨ ÏöîÏùº (0: ÏùºÏöîÏùº, 1: ÏõîÏöîÏùº, ...)
    const diff = currentDate.getDate() - day + (day == 0 ? -6 : 1); // Ïù¥Î≤à Ï£º ÏõîÏöîÏùº Íµ¨ÌïòÍ∏∞
    currentDate.setDate(diff);
    return currentDate;
  };

  // ÌïòÏù¥ÎùºÏù¥Ìä∏ ÏÉâÏÉÅ Ï†ÅÏö© (Ïòà: ÏôÑÎ£åÎêú Ï£ºÏ∞® ÌïòÏù¥ÎùºÏù¥Ìä∏)
  const highlightWeek = (date) => {
    const weekNumber = getWeekNumber(date);
    const currentDate = new Date();
    const currentWeek = getWeekNumber(currentDate);

    // ÌòÑÏû¨ Ï£ºÏôÄ ÏôÑÎ£åÎêú Ï£ºÍ∞Ä ÎèôÏùºÌïòÎ©¥ ÌïòÏù¥ÎùºÏù¥Ìä∏
    return weekNumber === currentWeek ? 'highlight-week' : '';
  };

  return (
    <div className="App">
      <h1>Ï±åÎ¶∞ÏßÄ Í¥ÄÎ¶¨ ÌéòÏù¥ÏßÄ</h1>
      
      <Calendar
        onChange={handleDateChange}
        value={date}
        tileContent={tileContent}  // ÎÇ†ÏßúÏóê Ìï¥Îπó Î°úÍ≥† ÌëúÏãú
        tileClassName={({ date, view }) => highlightWeek(date)}  // Ï£ºÏ∞® ÌïòÏù¥ÎùºÏù¥Ìä∏
      />
      
      <style>
        {`
          .habit-icon {
            font-size: 20px;
            text-align: right;
          }
        .react-calendar__tile {
            color: #4caf50;  /* Í∏∞Î≥∏ Í∏ÄÏî® ÏÉâÏÉÅ Î≥ÄÍ≤Ω (Ïòà: ÎÖπÏÉâ) */
          }
          .highlight-week {
            background-color: #ffeb3b;  // ÌïòÏù¥ÎùºÏù¥Ìä∏ ÏÉâÏÉÅ
          }
        `}
      </style>
    </div>
  );
};

export default ChallengePage;
