    endOfWeek.setDate(endOfWeek.getDate() + 6); // Ìï¥Îãπ Ï£ºÏùò ÏùºÏöîÏùº
  
    let completedDays = 0;
    for (
      let d = new Date(startOfWeek);
      d <= endOfWeek;
      d.setDate(d.getDate() + 1)
    ) {
      const dateKey = formatDateToKey(d); // Î°úÏª¨ ÏãúÍ∞ÑÏùÑ Í∏∞Î∞òÏúºÎ°ú ÎÇ†ÏßúÎ•º ÏÉùÏÑ±
      if (dailyChallenges[dateKey]) {
        completedDays += 1;
      }
    }
  
    return completedDays >= 5; // Ïòà: 5Ïùº Ïù¥ÏÉÅ Îã¨ÏÑ±Ìï¥Ïïº Ï£ºÍ∞Ñ Ï±åÎ¶∞ÏßÄ Îã¨ÏÑ±
  };
  

  // Í∞Å ÎÇ†ÏßúÏùò Ïä§ÌÉÄÏùº Í≤∞Ï†ï
  const tileClassName = ({ date, view }) => {
    if (view === 'month' && isWeeklyChallengeCompleted(date)) {
        // console.log(date);
      return 'react-calendar__tile--highlight';
    }
    return '';
  };

  // Í∞Å ÎÇ†ÏßúÏóê ÏïÑÏù¥ÏΩò Ï∂îÍ∞Ä
  const tileContent = ({ date, view }) => {
    if (view === 'month') {
    //   const dateKey = date.toISOString().split('T')[0]; // YYYY-MM-DD ÌòïÏãù
    const dateKey = formatDateToKey(date); // Î°úÏª¨ ÏãúÍ∞ÑÏùÑ Í∏∞Î∞òÏúºÎ°ú ÎÇ†ÏßúÎ•º ÏÉùÏÑ±
    //   console.log(dateKey);
      if (dailyChallenges[dateKey]) {
        return <img
        src={habitIcon} // ÏÑ±Í≥µÌïú ÎÇ†ÏßúÏóê habitIcon ÏÇ¨Ïö©
        alt="Ï±åÎ¶∞ÏßÄ Îã¨ÏÑ±"
      />; // Îã¨ÏÑ± ÏïÑÏù¥ÏΩò
      }
    //   return <span className="tile-icon">‚úñ</span>; // ÎØ∏Îã¨ÏÑ± ÏïÑÏù¥ÏΩò
    }
    return null;
  };

  const handleDayFormat = (locale, date) => {
    return date.getDate(); // ÎÇ†ÏßúÎßå Î∞òÌôò
  };

  const [activeStartDate, setActiveStartDate] = useState(new Date()); // ÌôúÏÑ±ÌôîÎêú ÎÇ†Ïßú

  // Ïù¥Ï†Ñ Îã¨Î°ú Ïù¥Îèô
  const handlePrevMonth = () => {
    const prevMonth = new Date(activeStartDate);
    prevMonth.setMonth(activeStartDate.getMonth() - 1);
    setActiveStartDate(prevMonth);
  };

  // Îã§Ïùå Îã¨Î°ú Ïù¥Îèô
  const handleNextMonth = () => {
    const nextMonth = new Date(activeStartDate);
    nextMonth.setMonth(activeStartDate.getMonth() + 1);
    setActiveStartDate(nextMonth);
  };

  return (
    <div className='challengePage'>
      <div className='top'>
        {/* <img
                className="settings-back-icon"
                alt="Back"
                src={back}
                onClick={() => navigate(-1)} // Îí§Î°úÍ∞ÄÍ∏∞
              /> */}
        <h1 className='title'>Ï±åÎ¶∞ÏßÄ</h1>
      </div>
      <div className="App">
        <div className='manage'>
          {/* <div className="settings-header">
            <img
              className="settings-back-icon"
              alt="Back"
              src={back}
              onClick={() => navigate(-1)} // Îí§Î°úÍ∞ÄÍ∏∞
            />
            <span className="settings-title"></span>
          </div>
          <h1>Ï±åÎ¶∞ÏßÄ Í¥ÄÎ¶¨</h1> */}
            {/* Ïª§Ïä§ÌÖÄ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
          <div className="custom-header">
            <div className='month'>
              <button onClick={handlePrevMonth} className="nav-button"><img src={left}></img></button>
              <span className="month-label">{activeStartDate.getMonth() + 1}Ïõî</span>
              <button onClick={handleNextMonth} className="nav-button"><img src={right}></img></button>
            </div>
            <button className="camera-button" onClick={handleCameraClick}>üì∑</button> {/* Ïπ¥Î©îÎùº Î≤ÑÌäº */}
          </div>
          <StyledCalendar
            locale="en-US" // ÏòÅÏñ¥ ÏïΩÏûêÎ°ú ÌëúÏãú
            activeStartDate={activeStartDate} // ÌôúÏÑ±ÌôîÎêú ÏãúÏûë ÎÇ†Ïßú
            onActiveStartDateChange={({ activeStartDate }) => setActiveStartDate(activeStartDate)}
            onChange={handleDateChange}
            value={date}
            tileContent={tileContent} // ÎÇ†ÏßúÎ≥Ñ ÏïÑÏù¥ÏΩò ÌëúÏãú
            tileClassName={tileClassName} // Ï£ºÍ∞Ñ Îã¨ÏÑ± Ïó¨Î∂Ä ÌïòÏù¥ÎùºÏù¥Ìä∏
            formatDay={handleDayFormat} // ÎÇ†Ïßú Ìè¨Îß∑ ÏàòÏ†ï
            showNavigation={false}
          />
        </div>
      </div>
    </div>
  );
};

export default ChallengePage;
