import React from 'react';
import Calendar from 'react-calendar';
import styled from 'styled-components';
import 'react-calendar/dist/Calendar.css';

const StyledCalendar = styled(Calendar)`
  width: 100%; /* 캘린더를 부모 요소의 너비에 맞게 설정 */
  max-width: 600px; /* 캘린더의 최대 너비를 제한 */
  margin: 0 auto; /* 중앙 정렬 */
//   border: none; /* 기본 테두리 제거 */
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1); /* 그림자 추가 (선택 사항) */

  .react-calendar__tile {
    background-color: #fdfdfd;
    color: #8E8E8E;
  }

  .react-calendar__navigation {
  display: flex; /* flex를 제거하고 버튼 크기 고정 */
  
}

  .react-calendar__tile--now {
    background-color: #ffeb3b;
    font-weight: bold;
    color: #000;
  }

  .react-calendar__tile--active {
    background-color: #00C5A1;
    color: white;
  }

  .react-calendar__navigation__label {
    font-size: 16px;
    font-weight: bold;
    color: #00C5A1;
  }

  button {
    width: auto;
  }

  .react-calendar__navigation__arrow {
    background-color: transparent;
    border: none;
    color: #00C5A1;
  }

  .current-month {
    color: #000; /* 선택된 달에 속한 날짜의 색상 */
    font-weight: bold; /* 선택된 달에 속한 날짜를 강조 */
  }

  .other-month {
    color: #ccc; /* 다른 달에 속한 날짜의 색상 */
  }
`;


const ChallengePage = () => {
  const [date, setDate] = React.useState(new Date());

  const handleDateChange = (newDate) => {
    setDate(newDate);
  };

  const tileContent = ({ date, view }) => {
    if (view === 'month') {
      return <span>{date.getDate()}</span>; // 날짜의 숫자만 표시
    }
    return null;
  };

  const tileClassName = ({ date, view }) => {
    if (view === 'month') {
      const selectedMonth = date.getMonth();
      const selectedYear = date.getFullYear();
      const currentMonth = new Date().getMonth();
      const currentYear = new Date().getFullYear();

      // 현재 선택된 달과 연도를 기준으로 스타일 적용
      return date.getMonth() === currentMonth && date.getFullYear() === currentYear
        ? 'current-month'
        : 'other-month';
    }
    return '';
  };


  return (
    <div className="App">
      <h1>챌린지 관리</h1>

      {/* StyledCalendar 사용 */}
      <StyledCalendar
        onChange={handleDateChange}
        value={date}
        tileContent={tileContent} // 날짜 표시 커스터마이징
        // tileClassName={tileClassName}
      />
    </div>
  );
};

export default ChallengePage;
