import React from 'react';
import './LoginPage.css';
import KakaoLogin from 'react-kakao-login'; // 카카오 로그인 라이브러리 임포트

const LoginPage = () => {
  const handleKakaoSuccess = (response) => {
    console.log('카카오 로그인 성공:', response);
    const idToken = response.access_token  // 엑세스 토큰 백엔드로 전달
    alert(`카카오 로그인 성공! 닉네임: ${response.profile.properties.nickname}`);
  };

  const handleKakaoFailure = (error) => {
    console.error('카카오 로그인 실패:', error);
    alert('카카오 로그인에 실패했습니다.');
  };

  return (
    <div className="login-container">
      <h1 className="title">한 손에 들고 먹는 건강한 습관</h1>
      <img
        src="./Hab-eat.png" // 로고 이미지 경로 (public 폴더에 logo.png 추가)
        alt="App Logo"
        className="logo"
      />
      <p className="subtitle">여러분의 식단, 같이 찍어봐요!</p>
      <div className="button-container">
        {/* 네이버 버튼 */}
        <button className="login-button naver">네이버로 시작하기</button>

        {/* 카카오 로그인 버튼 */}
        <KakaoLogin
          token="6668803810c5ef2fa19eb935be883747" // 카카오 개발자 콘솔에서 발급받은 JavaScript 키
          onSuccess={handleKakaoSuccess}
          onFail={handleKakaoFailure}
          onLogout={() => console.log('카카오 로그아웃')}
          render={(props) => (
            <button className="login-button kakao" onClick={props.onClick}>
              카카오로 시작하기
            </button>
          )}
        />
      </div>
    </div>
  );
};

export default LoginPage;
