import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';  // 기본 스타일 import

const ChallengePage = () => {
  const [date, setDate] = useState(new Date()); // 기본 날짜를 오늘로 설정

  const handleDateChange = (newDate) => {
    setDate(newDate);  // 사용자가 선택한 날짜로 상태 업데이트
  };

  return (
    <div className="App">
      <h1>챌린지 관리 페이지</h1>

      {/* 기본 달력 컴포넌트 */}
      <Calendar
        onChange={handleDateChange}  // 날짜 변경 시 처리할 함수
        value={date}  // 현재 선택된 날짜
      />
    </div>
  );
};

export default ChallengePage;
