import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import UserInfoPage from './pages/UserInfoPage';
import NutritionPage from './pages/NutritionPage';
import RedirectHandler from './pages/RedirectHandler';
import LoginPage from './pages/LoginPage';

const App = () => {
  const [isInfoComplete, setIsInfoComplete] = useState(false);

  const handleInfoComplete = () => {
    setIsInfoComplete(true);
  };

  const handleLoginSuccess = () => {
    setIsInfoComplete(false); // 로그인 후 UserInfoPage로 이동
  };

  return (
    <Router>
      <Routes>
        {/* 로그인 페이지 */}
        <Route path="/login" element={<LoginPage />} />

        {/* 카카오 로그인 Redirect 처리 */}
        <Route
          path="/redirect"
          element={<RedirectHandler onLoginSuccess={handleLoginSuccess} />}
        />

        {/* UserInfoPage 및 라우팅 */}
        <Route
          path="/"
          element={
            isInfoComplete ? <Navigate to="nutrition" /> : <UserInfoPage onComplete={handleInfoComplete} />
          }
        />
        <Route path="/nutrition" element={<NutritionPage />} />
        <Route path="/habit" element={<div>습관 페이지 준비중...</div>} />
        {/* <Route path="/mypage" element={<MyPage />} /> */}
      </Routes>
    </Router>
  );
};

export default App;
