import React, { useState, useEffect } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import "./HabitPage.css";

export const HabitPage = () => {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const completedId = searchParams.get('completedId');
  
  const [challenges, setChallenges] = useState([
    { id: 1, title: '매일 운동', status: 'participating' },
    { id: 2, title: '건강식 챌린지', status: 'available' },
    { id: 3, title: '미라클 모닝', status: 'available' },
  ]);

  const isMonday = new Date().getDay() === 1;

  // 챌린지 완료 후 상태 업데이트 (completedId가 있을 때 상태 변경)
  useEffect(() => {
    if (completedId) {
      const challengeExists = challenges.some(challenge => challenge.id === parseInt(completedId));
      if (challengeExists) {
        setChallenges((prevChallenges) =>
          prevChallenges.map((challenge) =>
            challenge.id === parseInt(completedId)
              ? { ...challenge, status: 'participating' }  // 해당 챌린지만 상태 변경
              : challenge
          )
        );
      }
    }
  }, [completedId, challenges]);

  const joinChallenge = (id) => {
    if (isMonday) {
      setChallenges((prevChallenges) =>
        prevChallenges.map((challenge) =>
          challenge.id === id && challenge.status !== 'participating'
            ? { ...challenge, status: 'participating' }
            : challenge
        )
      );
      navigate(`/join?id=${id}`);
    } else {
      alert("월요일에 참여해주세요!");
    }
  };

  // 챌린지 관리 페이지로 이동하는 함수 (참여 중인 챌린지만)
  const manageChallenge = (id) => {
    const challenge = challenges.find(challenge => challenge.id === id);
    if (challenge && challenge.status === 'participating') {
      navigate(`/challenge/${id}`);
    } else {
      alert("참여 중인 챌린지만 관리할 수 있습니다.");
    }
  };

  const participatingChallenges = challenges.filter(challenge => challenge.status === 'participating');
  const availableChallenges = challenges.filter(challenge => challenge.status === 'available');

  return (
    <div className="App">
      <h1>습관 챌린지</h1>
      
      <div className="section">
        <h2>참여 중</h2>
        <ul>
          {participatingChallenges.length > 0 ? (
            participatingChallenges.map(challenge => (
              <li key={challenge.id}>
                <button disabled onClick={() => manageChallenge(challenge.id)}>
                  {challenge.title}
                </button>
              </li>
            ))
          ) : (
            <li>
              <p className='al'>참여 중인 챌린지가 없습니다.</p>
            </li>
          )}
        </ul>
      </div>

      <div className="section">
        <h2>참여 가능</h2>
        <ul>
          {availableChallenges.length > 0 ? (
            availableChallenges.map(challenge => (
              <li key={challenge.id}>
                <button onClick={() => joinChallenge(challenge.id)}>
                  {challenge.title}
                </button>
              </li>
            ))
          ) : (
            <li>
              <p className='al'>참여 가능한 챌린지가 없습니다.</p>
            </li>
          )}
        </ul>
      </div>
    </div>
  );
};

export default HabitPage;
